<?phprequire_once FILE_ACCESS_CORE_CODE.'/Framework/MVC_superClasses_Core/viewSuperClass_Core/viewSuperClass_Core.php';class view extends viewSuperClass_Core{	private $sidenav_data;	private $article_content;	private $is_mobile;	public  $articleDetailMobile;		public function setIsMobile($is_mobile)	{		if ($is_mobile)			$this->is_mobile = 'listing';		else			$this->is_mobile = 'article';	}		public function getIsMobile()	{		return $this->is_mobile;	}	//-------------------------------------------------------------------------------------------------------------	public function setArticleContent($article_content)	{		if ($article_content !== FALSE)			$this->article_content = $article_content;		elseif ($article_content === 'mobile')			$this->article_content	=	FALSE;		else			header("Location: /404");	}	//-------------------------------------------------------------------------------------------------------------	public function setSideNavData($sidenav_data)	{		$this->sidenav_data = $sidenav_data;	}	//-------------------------------------------------------------------------------------------------------------		public function getArticleContent()	{		return $this->article_content;	}	//-------------------------------------------------------------------------------------------------------------		public function getSideNavData()	{		return $this->sidenav_data;	}//-------------------------------------------------------------------------------------------------------------	public function displayPageNavigation()	{		$content= $this->renderTemplate('Project/1_Website/Design/Pages/primary/products/templates/navigation.phtml');		response::getInstance()->addContentToTree(array('SECONDLEVELNAVIGATION'=>$content));	}	//-------------------------------------------------------------------------------------------------------------		public function displayPageContent($articleDetailMobile)	{		$this->articleDetailMobile = $articleDetailMobile;				$content= $this->renderTemplate('Project/1_Website/Design/Pages/primary/products/templates/page_content.phtml');		response::getInstance()->addContentToTree(array('PAGECONTENT'=>$content));			}}?>