<?phprequire_once FILE_ACCESS_CORE_CODE.'/Framework/MVC_superClasses_Core/viewSuperClass_Core/viewSuperClass_Core.php';class view extends viewSuperClass_Core{	public function displayMainTemplate()	{		$content = $this->renderTemplate("Project/1_Website/Design/Pages/primary/features/templates/page_template.phtml");		response::getInstance()->addContentToTree(array("MAIN_CONTENT" => $content));				$content = $this->renderTemplate("Project/1_Website/Design/Pages/primary/features/templates/css_links.phtml");		response::getInstance()->addContentToStack("local_css", array("" => $content) );				$content = $this->renderTemplate("Project/1_Website/Design/Pages/primary/features/templates/inpage_javascript.js");		response::getInstance()->addContentToStack("local_javascript_bottom", array("" => $content) );	}}?>