<?phprequire_once FILE_ACCESS_CORE_CODE.'/Framework/MVC_superClasses_Core/controllerSuperClass_Core/controllerSuperClass_Core.php';require_once('Model.php');require_once('View.php');require_once "Project/Model/User_Account/user_account.php";require_once 'Project/1_Website/Code/Modules/userSession.php';
class controller extends controllerSuperClass_Core
{	public function indexAction()	{		$view = new view();		$view->renderAll();	}		public function loginAction()	{		if(isset($_POST["submit"]))		{			$email 	  = $_POST["email"];			$password = sha1(md5($_POST["password"]));			//check if user is existing in the database			$user = user_account::selectLoggedIn($email, $password);						//save session if session is existing			if($user)			{				$sessions = array(					"user_id"    => $user["user_id"],					"email"		 => $email,					"password"	 => $password,					"user_type"  => $user["user_type"],					"name"		 => $user["first_name"] . " " . $user["last_name"]				);								userSession::saveSession($sessions);								header("Location: /profile");			}		}	}		public function logoutAction()	{		userSession::unsetSession();		header("Location: /home");	}	}?>