<?phprequire_once(FILE_ACCESS_CORE_CODE.'/Framework/MVC_superClasses_Core/viewSuperClass_Core/viewSuperClass_Core.php');/** * The Main Layout gets all the content for the areas outside the main focus. * Eg. if you are on www.MyResort.com/accommodation or even www.MyResort.com,  * the focus will be on a page * but you have other tasks like header photo, logo, navigation * these will be needed on most pages.  *  */class mainView extends viewSuperClass_Core{	public function getMainLayout() 	{		//HTML5 SHIV ======================================================================================================		$content = $this->renderTemplate('Project/'.DOMAIN.'/Design/Main_Layout/templates/html5Shiv.phtml');		response::getInstance()->addContentToStack('global_javascript_top',array('HTML5 SHIV'=>$content),'TOP');		//Myk placed this here to make older browsers which do not support HTML5 function properly				//JQUERY LIBRARY =======================================================================================================		$content = $this->renderTemplate(FILE_ACCESS_CORE_DESIGN.'/Plugins/JQuery/templates/jquery_link.phtml');		response::getInstance()->addContentToStack('global_javascript_top',array('JQUERY'=>$content),'TOP');				//JQUERY AJAX =======================================================================================================		$content = $this->renderTemplate(FILE_ACCESS_CORE_DESIGN.'/Plugins/Ajax/templates/jquery_link.phtml');		response::getInstance()->addContentToStack('global_javascript_top',array('JQUERYAJAX'=>$content));					//JQUNIFORM =======================================================================================================		$content = $this->renderTemplate('Project/2_Enterprise/Design/Plugins/JQuery_Uniform/templates/jquery_link.phtml');		response::getInstance()->addContentToStack('global_javascript_bottom',array('JQUERYUNIFORM'=>$content));				$content = $this->renderTemplate('Project/2_Enterprise/Design/Plugins/JQuery_Uniform/templates/css_links.phtml');		response::getInstance()->addContentToStack('global_css',array('JQUERYUNIFORM CSS'=>$content));					//JTEXTAREA EXPANDER =======================================================================================================		$content = $this->renderTemplate('Project/2_Enterprise/Design/Plugins/TextareaExpander/templates/JtextareaExpander_link.phtml');		response::getInstance()->addContentToStack('global_javascript_bottom',array('TEXTAREA EXPANDER'=>$content));					//Bootstrap =======================================================================================================		$content = $this->renderTemplate('Project/2_Enterprise/Design/Plugins/bootstrap/templates/bootstrap_js_link.phtml');		response::getInstance()->addContentToStack('global_javascript_top',array('BOOTSTRAP JS'=>$content));				$content = $this->renderTemplate('Project/2_Enterprise/Design/Plugins/bootstrap/templates/bootstrap_css_link.phtml');		response::getInstance()->addContentToStack('global_css',array('BOOTSTRAP CSS'=>$content));				//Validation Engine =======================================================================================================		$content = $this->renderTemplate('Project/2_Enterprise/Design/Plugins/ValidationEngine/templates/validationEngine_link.phtml');		response::getInstance()->addContentToStack('global_javascript_bottom',array('JQUERY VALIDATIONENGINE JS'=>$content));				$content = $this->renderTemplate('Project/2_Enterprise/Design/Plugins/ValidationEngine/templates/validationEngine_link.phtml');		response::getInstance()->addContentToStack('global_css',array('JQUERY VALIDATIONENGINE CSS'=>$content));				//TEXT EDITOR =======================================================================================================		$content = $this->renderTemplate('Project/2_Enterprise/Design/Plugins/CKEditor/css_link.phtml');		response::getInstance()->addContentToStack('global_css',array('CKEditor CSS'=>$content));				//JTEXTAREA EXPANDER =======================================================================================================		$content = $this->renderTemplate('Project/2_Enterprise/Design/Main_Layout/scripts/popupDialogs.phtml');		response::getInstance()->addContentToStack('global_javascript_top',array('POPUP'=>$content));				//JQUERY UI LIBRARY =======================================================================================================		$content = $this->renderTemplate(FILE_ACCESS_CORE_DESIGN.'/Plugins/JQueryUi/templates/jquery_ui_link.phtml');		response::getInstance()->addContentToStack('global_javascript_bottom',array('JQUERYUI'=>$content));				$content = $this->renderTemplate(FILE_ACCESS_CORE_DESIGN.'/Plugins/JQueryUi/templates/css_link.phtml');		response::getInstance()->addContentToStack('global_css',array('JQUERYUI CSS'=>$content));				//JCROP =======================================================================================================		$content = $this->renderTemplate('Project/2_Enterprise/Design/Plugins/JCrop/templates/jquery_link.phtml');		response::getInstance()->addContentToStack('global_javascript_bottom',array('JCROP'=>$content));				$content = $this->renderTemplate('Project/2_Enterprise/Design/Plugins/JCrop/templates/css_links.phtml');		response::getInstance()->addContentToStack('global_css',array('JCROP CSS'=>$content));				//-----------------------------------------------------------------------------------------------------------------				$content = $this->renderTemplate("Project/".DOMAIN."/Design/Main_Layout/templates/global_js_links.phtml");		response::getInstance()->addContentToTree(array('JAVASCRIPTS'=>$content));				$content = $this->renderTemplate('Project/'.DOMAIN.'/Design/Main_Layout/templates/main.phtml');		response::getInstance()->addContentToTree(array('TOPLEVEL'=>$content));				$content = $this->renderTemplate("Project/".DOMAIN."/Design/Main_Layout/templates/global_css_links.phtml");		response::getInstance()->addContentToStack('global_css',array('MAIN LAYOUT CSS'=>$content));					$content = $this->renderTemplate("Project/".DOMAIN."/Design/Main_Layout/templates/global_inpage_javascript.js");		response::getInstance()->addContentToStack('local_javascript_bottom',array('MAIN INPAGE JAVASCRIPT'=>$content));			}	//--------------------------------------------------------------------------------		public function displayLoginForm()	{		$content = $this->renderTemplate("Project/".DOMAIN."/Design/Main_Layout/login_templates/js_and_css_links.phtml");		response::getInstance()->addContentToStack('local_css',array('LOGIN JS AND CSS'=>$content));				$content = $this->renderTemplate("Project/".DOMAIN."/Design/Main_Layout/login_templates/login_form.phtml");		response::getInstance()->addContentToTree(array('MAIN_CONTENT'=>$content));	}}?>