<?phprequire_once FILE_ACCESS_CORE_CODE.'/Modules/Database/starfishDatabase.php';class image{	private $image_id;	private $image_title;	private $album_id;	private $size_id;	private $filename_ext;	private $image_caption;	private $filename;	private $dimensions;	private $path;	private $date_created;		//setter	//-----------------------------------------------------------------------------------------------------------------------------------	public function setImageId($image_id)	{		$this->image_id = $image_id;	}		public function setAlbumId($album_id)	{		$this->album_id = $album_id;	}		public function setPath($path)	{		$this->path = $path;	}		public function setSizeId($size_id)	{		$this->size_id = $size_id;	}		public function setImageCaption($image_caption)	{		$this->image_caption = $image_caption;	}		public function setFileName($filename)	{		$this->filename = $filename;	}		public function setImageTitle($image_title)	{		$this->image_title = $image_title;	}		public function setFileNameExt($filename_ext)	{		$this->filename_ext = $filename_ext;	}		public function setDateCreated($date_created)	{		$this->date_created = $date_created;	}			//getter	//-----------------------------------------------------------------------------------------------------------------------------------	public function getImageId()	{		return $this->image_id;	}		public function getAlbumId()	{		return $this->album_id;	}		public function getSizeId()	{		return $this->size_id;	}		public function getPath()	{		return $this->path;	}		public function getImageCaption()	{		return $this->image_caption;	}		public function getImageTitle()	{		return $this->image_title;	}		public function getFileName()	{		return $this->filename;	}		public function getFileNameExt()	{		return $this->filename_ext;	}		public function getDateCreated()	{		return $this->date_created;	}			public function select()	{		try		{			$pdo_connection = starfishDatabase::getConnection();			$sql = "SELECT						*					FROM						images					WHERE						image_id = :image_id					";				$pdo_statement = $pdo_connection->prepare($sql);				$pdo_statement->bindParam(':image_id', $this->image_id, PDO::PARAM_INT);				$pdo_statement->execute();				$result = $pdo_statement->fetch(PDO::FETCH_ASSOC);								$this->album_id				= $result['album_id'];				$this->size_id				= $result['size_id'];				$this->image_title			= $result['image_title'];				$this->image_caption		= $result['image_caption'];				$this->filename_ext			= $result['filename_ext'];				$this->filename				= $result['filename'];				$this->path					= $result['path'];		}		catch(PDOException $pdoe)		{			throw new Exception($pdoe);			}	}		//--------------------------------------------------------------------------------------------------------------------	public function insert()	{		try		{			$pdo_connection = starfishDatabase::getConnection();			$sql = "INSERT INTO						images						(							`album_id`,							`size_id`,							`image_title`,							`image_caption`,							`filename`,							`filename_ext`,							`path`						)						VALUES						(							:album_id,							:size_id,							:image_title,							:image_caption,							:filename,							:filename_ext,							:path						)					";				$pdo_statement = $pdo_connection->prepare($sql);				$pdo_statement->bindParam(':album_id', $this->album_id, PDO::PARAM_INT);				$pdo_statement->bindParam(':size_id', $this->size_id, PDO::PARAM_INT);				$pdo_statement->bindParam(':image_title', $this->image_title, PDO::PARAM_STR);				$pdo_statement->bindParam(':image_caption', $this->image_caption, PDO::PARAM_STR);				$pdo_statement->bindParam(':filename', $this->filename, PDO::PARAM_STR);				$pdo_statement->bindParam(':filename_ext', $this->filename_ext, PDO::PARAM_STR);				$pdo_statement->bindParam(':path', $this->path, PDO::PARAM_STR);								$pdo_statement->execute();				$this->image_id = $pdo_connection->lastInsertId();		}		catch(PDOException $pdoe)		{			throw new Exception($pdoe);			}	}	//--------------------------------------------------------------------------------------------------------------------	public function update()	{		try		{			$pdo_connection = starfishDatabase::getConnection();			$sql = "UPDATE						images					SET						album_id		= :album_id,						size_id			= :size_id,						image_caption	= :image_caption,						filename		= :filename,						image_title     = :image_title					WHERE						image_id = :image_id					";				$pdo_statement = $pdo_connection->prepare($sql);				$pdo_statement->bindParam(':image_id', $this->image_id, PDO::PARAM_INT);				$pdo_statement->bindParam(':album_id', $this->album_id, PDO::PARAM_INT);				$pdo_statement->bindParam(':size_id', $this->size_id, PDO::PARAM_INT);				$pdo_statement->bindParam(':image_caption', $this->image_caption, PDO::PARAM_STR);				$pdo_statement->bindParam(':image_title', $this->image_title, PDO::PARAM_STR);				$pdo_statement->bindParam(':filename', $this->filename, PDO::PARAM_STR);				$pdo_statement->execute();		}		catch(PDOException $pdoe)		{			throw new Exception($pdoe);			}	}		//--------------------------------------------------------------------------------------------------------------------	public function delete()	{		try		{			$pdo_connection = starfishDatabase::getConnection();						$sql = "DELETE FROM						images					WHERE						image_id = :image_id					";							$pdo_statement = $pdo_connection->prepare($sql);				$pdo_statement->bindParam(':image_id', $this->image_id, PDO::PARAM_INT);				$pdo_statement->execute();		}		catch(PDOException $pdoe)		{			throw new Exception($pdoe);			}	}		//--------------------------------------------------------------------------------------------------------------------		public function updateOneColumn($column, $value)	{		try		{			$pdo_connection = starfishDatabase::getConnection();					$sql = "UPDATE								images							SET								$column = :$column							WHERE								image_id = :image_id							";					$pdo_statement = $pdo_connection->prepare($sql);			$pdo_statement->bindParam(':image_id', $this->image_id, PDO::PARAM_INT);			$pdo_statement->bindParam(":$column", $value, PDO::PARAM_STR);			$pdo_statement->execute();		}		catch(PDOException $pdoe)		{			throw new Exception($pdoe);		}	}		}