<?phprequire_once FILE_ACCESS_CORE_CODE.'/Modules/Database/starfishDatabase.php';require_once 'image.php';
class images{
	private $array_of_images;	private $album_id;	private $size_id;			public function getArrayOfImages()	{		return $this->array_of_images;	}		//----------------------------------------------------------------------------------------------------------		public function select()	{		try		{			$pdo_connection = starfishDatabase::getConnection();			$sql = "SELECT						*					FROM						images					";						$pdo_statement = $pdo_connection->prepare($sql);			$pdo_statement->execute();			$results = $pdo_statement->fetchAll(PDO::FETCH_ASSOC);					foreach($results as $result)			{				$image = new image();								$image->setAlbumId($result["album_id"]);				$image->setImageId($result["image_id"]);				$image->setSizeId($result["size_id"]);				$image->setImageTitle($result["image_title"]);				$image->setImageTitle($result["image_title_element"]);				$image->setImageCaption($result["image_caption"]);				$image->setFileName($result["filename"]);				$image->setFileNameExt($result["filename_ext"]);				$image->setDateCreated($result["date_created"]);				$image->setPath($result["path"]);				$this->array_of_images[] = $image;			}			}		catch(PDOException $pdoe)		{			throw new Exception($pdoe);			}	}	//----------------------------------------------------------------------------------------------------------
		public function deleteByColumn($column, $value)	{		try		{			$pdo_connection = starfishDatabase::getConnection();			$sql = "DELETE FROM						images					WHERE						$column = :$column					";						$pdo_statement = $pdo_connection->prepare($sql);			$pdo_statement->bindParam(":$column", $value, PDO::PARAM_STR);			$pdo_statement->execute();		}		catch(PDOException $pdoe)		{			throw new Exception($pdoe);		}	}
	
}